using System;
using System.Collections.Generic;
using System.Text;
using NUnit.Framework;
using Org.BouncyCastle.Cms;
using Org.BouncyCastle.Cms.Tests;
using Org.BouncyCastle.Utilities.Encoders;

namespace UnitTestsExtensions.src.cms
{
    [TestFixture]
    public class KncaSignedDataTest
    {
        private static readonly byte[] content = Encoding.UTF8.GetBytes("test attribute");
        private static readonly byte[] rsaSignature = Base64.Decode("");
        private static readonly byte[] gostSignature = Base64.Decode("");

        [Test]
        public void TestRsaVerification()
        {
            var cms = new CmsSignedData(rsaSignature);
            if (cms.SignedContent == null)
            {
                cms = new CmsSignedData(new CmsProcessableByteArray(content), rsaSignature);
            }
            SignedDataTest.VerifySignatures(cms);
        }

        [Test]
        public void TestGostVerification()
        {
            var cms = new CmsSignedData(gostSignature);
            if (cms.SignedContent == null)
            {
                cms = new CmsSignedData(new CmsProcessableByteArray(content), gostSignature);
            }
            SignedDataTest.VerifySignatures(cms);
        }
    }
}
